                  {isLoading ? 'Starting...' : 'Start AI Session'}
                </button>

                <button 
                  className="btn btn-warning" 
                  onClick={stopAISession}
                  disabled={isLoading}
                >
                  {isLoading ? '⏳' : '🛑'}
                  {isLoading ? 'Stopping...' : 'Stop AI Session'}
                </button>
              </div>

              {/* Utility Buttons */}
              <div className="control-row">
                <button className="btn btn-secondary" onClick={testConnection}>
                  📡 Test Connection
                </button>

                <button className="btn btn-secondary" onClick={clearTranscriptions}>
                  🗑️ Clear All
                </button>
              </div>

              {/* Status Display */}
              <div className="status-display">
