                    min="16000" 
                    max="48000" 
                    step="8000" 
                    defaultValue="24000"
                    className="slider"
                    onChange={(e) => {
                      const value = parseInt(e.target.value);
                      e.target.nextElementSibling.textContent = `${value}Hz`;

                      // Update audio service settings
                      audioService.updateSettings({ sample_rate: value });

                      // Send to backend via WebSocket
                      websocketService.sendAudioSettings({ sample_rate: value });

                      addNotification(`🎵 Sample rate updated to ${value}Hz`, 'info');
                    }}
                  />
                  <span className="setting-value">24000Hz</span>
                </div>
              </div>

              <div className="setting-item">
                <label>VAD Threshold</label>
                <div className="setting-control">
                  <input 
                    type="range" 
                    min="0" 
                    max="1" 
                    step="0.1" 
                    defaultValue="0.2"
                    className="slider"
                    onChange={(e) => {
                      const value = parseFloat(e.target.value);
                      e.target.nextElementSibling.textContent = value;

                      // Update audio service settings
                      audioService.updateSettings({ vad_threshold: value });

                      // Send to backend via WebSocket
                      websocketService.sendAudioSettings({ vad_threshold: value });

                      addNotification(`🎤 VAD threshold updated to ${value}`, 'info');
                    }}
                  />
                  <span className="setting-value">0.2</span>
                </div>
              </div>

              <div className="setting-item">
                <label>Volume</label>
