                      audioService.updateSettings({ echo_cancellation: enabled });

                      // Send to backend via WebSocket
                      websocketService.sendAudioSettings({ echo_cancellation: enabled });

                      addNotification(`🔇 Echo cancellation ${enabled ? 'enabled' : 'disabled'}`, 'info');
                    }}
                  />
                </div>
              </div>

