                      // Update audio service settings
                      audioService.updateSettings({ noise_suppression: enabled });

                      // Send to backend via WebSocket
                      websocketService.sendAudioSettings({ noise_suppression: enabled });

                      addNotification(`🎵 Noise suppression ${enabled ? 'enabled' : 'disabled'}`, 'info');
                    }}
                  />
                </div>
