          justify-content: space-between;
          align-items: center;
          padding: var(--spacing-md);
          background: var(--bg-secondary);
          border-radius: var(--radius-md);
        }

        .setting-item label {
          font-weight: 500;
          color: var(--text-primary);
        }

        .setting-control {
          display: flex;
          align-items: center;
          gap: var(--spacing-sm);
        }

        .slider {
          width: 100px;
          height: 6px;
          border-radius: var(--radius-full);
          background: var(--border-color);
          outline: none;
          cursor: pointer;
        }

        .slider:hover {
          background: var(--border-dark);
        }

        .setting-value {
          min-width: 60px;
          text-align: right;
          color: var(--text-secondary);
        }

        .toggle {
          width: 20px;
          height: 20px;
          cursor: pointer;
        }

        .status-panel {
          display: flex;
          flex-direction: column;
          gap: var(--spacing-md);
        }

        .status-row {
          display: flex;
          justify-content: space-between;
          padding: var(--spacing-md);
          background: var(--bg-secondary);
          border-radius: var(--radius-md);
        }

        .status-value {
          font-weight: bold;
        }

        .status-value.connected {
          color: var(--success-color);
        }

        .status-value.disconnected {
          color: var(--danger-color);
        }

        .status-value.primary {
          color: var(--primary-color);
        }

        .status-value.warning {
          color: var(--warning-color);
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.5; }
        }

        @media (max-width: 768px) {
          .grid {
            grid-template-columns: 1fr;
            gap: var(--spacing-lg);
          }

          .control-row {
            flex-direction: column;
          }

          .btn {
            width: 100%;
          }

          .notifications-container {
            right: 10px;
            left: 10px;
            max-width: none;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
