import React, { useState, useEffect, useRef } from 'react';
import NotificationSystem from './components/NotificationSystem';
import { handleError, ErrorTypes } from './utils/errorHandler';

function App() {
  // Basic state for functionality
  const [isRecording, setIsRecording] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [transcriptions, setTranscriptions] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // WebSocket connection
  const wsRef = useRef(null);
  const recordingIntervalRef = useRef(null);

  // Notification system
  const addNotification = (message, type = 'info', duration = 5000) => {
    const id = Date.now();
    const notification = {
      id,
      message,
      type,
      timestamp: new Date()
    };

    setNotifications(prev => [...prev, notification]);

    // Auto-remove notification after duration
    setTimeout(() => {
      removeNotification(id);
    }, duration);
  };

  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  useEffect(() => {
    // Initialize WebSocket
    connectWebSocket();

    // Cleanup
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
      if (recordingIntervalRef.current) {
        clearInterval(recordingIntervalRef.current);
      }
    };
  }, []);

  // Error handling wrapper
  const handleAppError = (error, context = 'Operation') => {
    const errorInfo = handleError(error, context, ErrorTypes.GENERAL);
    addNotification(errorInfo.userMessage, 'warning', 8000);
  };

  const connectWebSocket = () => {
    try {
      const sessionId = `session_${Date.now()}`;
      const ws = new WebSocket(`ws://localhost:8000/ws/${sessionId}`);

      ws.onopen = () => {
        console.log('‚úÖ WebSocket connected');
        setIsConnected(true);
        addNotification('üîó Connected to backend server', 'success');
      };

      ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          handleWebSocketMessage(data);
        } catch (error) {
          handleAppError(error, 'Message parsing');
        }
      };

      ws.onclose = () => {
        console.log('‚ùå WebSocket disconnected');
        setIsConnected(false);
        addNotification('üîå Disconnected from backend server', 'info');
      };

      ws.onerror = (error) => {
        console.error('‚ùå WebSocket error:', error);
        setIsConnected(false);
        handleAppError(error, 'WebSocket connection');
      };

      wsRef.current = ws;
    } catch (error) {
      handleAppError(error, 'WebSocket setup');
    }
  };

  const handleWebSocketMessage = (data) => {
    console.log('üì® Received message:', data);

    try {
      switch (data.type) {
        case 'transcription':
          addTranscription(data.data);
          break;
        case 'sentiment':
          console.log('Sentiment data received:', data.data);
          break;
        case 'connection':
          console.log('Connection status:', data.status);
          break;
        default:
          console.log('Unknown message type:', data.type);
      }
    } catch (error) {
      handleError(error, 'Message handling');
    }
  };

  const getDevices = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('/api/devices');

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('üì± Devices loaded:', data);
      addNotification('üì± Audio/video devices loaded', 'success');
    } catch (error) {
      handleAppError(error, 'Device loading');
    } finally {
      setIsLoading(false);
    }
