        @media (max-width: 768px) {
          .grid {
            grid-template-columns: 1fr;
            gap: var(--spacing-lg);
          }

          .control-row {
            flex-direction: column;
          }

          .btn {
            width: 100%;
          }

          .notifications-container {
            right: 10px;
            left: 10px;
            max-width: none;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
